interface.signature:
POST /functions/v1/admin-override-resolution
Body: { market_id: string, new_outcome: "YES" | "NO" | "CANCELLED", reason: string }
Headers: { Authorization: Bearer <supabase-key> }
Response: { success: boolean, message: string }

-- Total markets
SELECT COUNT(*) FROM markets;

-- Active users (last 30 days)
SELECT COUNT(DISTINCT user_wallet) FROM (
  SELECT user_wallet FROM bets WHERE created_at > now() - interval '30 days'
  UNION
  SELECT voter_wallet FROM votes WHERE timestamp > now() - interval '30 days'
) as active_users;

-- 24h volume
SELECT COALESCE(SUM(amount), 0) FROM bets WHERE created_at > now() - interval '24 hours';

-- Dispute rate
SELECT (COUNT(*) FILTER (WHERE status = 'DISPUTE_WINDOW') * 100.0 /
        NULLIF(COUNT(*) FILTER (WHERE status IN ('RESOLVED', 'DISPUTE_WINDOW')), 0)) as dispute_rate
FROM markets;
