import { useState, useEffect, useCallback } from 'react'
import { supabase } from '@/lib/supabase'

export interface LeaderboardEntry {
  wallet_address: string
  activity_points: number
  total_bets: number
  win_rate: number
  total_profit: number
  total_volume: number
  markets_created?: number
}

interface UseLeaderboardDataReturn {
  data: LeaderboardEntry[]
  loading: boolean
  error: Error | null
  refetch: () => Promise<void>
}

export function useLeaderboardData(
  category: 'points' | 'win-rate' | 'volume' | 'creators'
): UseLeaderboardDataReturn {
  const [data, setData] = useState<LeaderboardEntry[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<Error | null>(null)

  const fetchLeaderboard = useCallback(async () => {
    try {
      setLoading(true)
      setError(null)

      let query = supabase.from('user_stats').select('*')

      // Apply category-specific sorting and filters
      switch (category) {
        case 'points':
          query = query.order('activity_points', { ascending: false })
          break
        case 'win-rate':
          query = query.gte('total_bets', 10) // Minimum threshold
                       .order('win_rate', { ascending: false })
          break
        case 'volume':
          query = query.order('total_volume', { ascending: false })
          break
        case 'creators':
          // For creators, we need to join with creator_stats
          const { data: creatorData, error: creatorError } = await supabase
            .from('creator_stats')
            .select('*')
            .order('markets_created', { ascending: false })
            .limit(100)

          if (creatorError) throw creatorError

          setData(creatorData as LeaderboardEntry[])
          setLoading(false)
          return
      }

      // Limit to top 100
      query = query.limit(100)

      const { data: leaderboardData, error: fetchError } = await query

      if (fetchError) throw fetchError

      setData(leaderboardData as LeaderboardEntry[])
    } catch (err) {
      console.error('Error fetching leaderboard:', err)
      setError(err instanceof Error ? err : new Error('Failed to fetch leaderboard'))
    } finally {
      setLoading(false)
    }
  }, [category])

  // Initial fetch
  useEffect(() => {
    fetchLeaderboard()
  }, [fetchLeaderboard])

  // Real-time subscription for updates
  useEffect(() => {
    const channel = supabase
      .channel('leaderboard-updates')
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'user_stats',
        },
        () => {
          // Refetch on any user_stats change
          fetchLeaderboard()
        }
      )
      .subscribe()

    return () => {
      supabase.removeChannel(channel)
    }
  }, [fetchLeaderboard])

  return {
    data,
    loading,
    error,
    refetch: fetchLeaderboard,
  }
}

import { useState, useEffect, useCallback } from 'react'
import { supabase } from '@/lib/supabase'

interface UserStats {
  wallet_address: string
  activity_points: number
  total_bets: number
  win_rate: number
  total_profit: number
  total_volume: number
  markets_created: number
}

interface RecentBet {
  id: string
  market_id: number
  outcome: 'YES' | 'NO'
  amount: number
  profit_loss: number | null
  created_at: string
  market: {
    id: string
    question: string
    status: string
    winning_outcome: string | null
  }
}

interface CreatedMarket {
  id: string
  market_id: number
  question: string
  status: string
  total_volume: number
  created_at: string
}

interface UseUserProfileReturn {
  stats: UserStats | null
  recentBets: RecentBet[]
  createdMarkets: CreatedMarket[]
  loading: boolean
  error: Error | null
}

export function useUserProfile(walletAddress: string): UseUserProfileReturn {
  const [stats, setStats] = useState<UserStats | null>(null)
  const [recentBets, setRecentBets] = useState<RecentBet[]>([])
  const [createdMarkets, setCreatedMarkets] = useState<CreatedMarket[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<Error | null>(null)

  const fetchUserProfile = useCallback(async () => {
    try {
      setLoading(true)
      setError(null)

      // Fetch user stats
      const { data: statsData, error: statsError } = await supabase
        .from('user_stats')
        .select('*')
        .eq('wallet_address', walletAddress)
        .single()

      if (statsError && statsError.code !== 'PGRST116') { // Ignore not found
        throw statsError
      }

      // Fetch recent bets
      const { data: betsData, error: betsError } = await supabase
        .from('bets')
        .select(`
          *,
          markets (
            id,
            question,
            status,
            winning_outcome
          )
        `)
        .eq('user_wallet', walletAddress)
        .order('created_at', { ascending: false })
        .limit(10)

      if (betsError) throw betsError

      // Fetch created markets
      const { data: marketsData, error: marketsError } = await supabase
        .from('markets')
        .select('*')
        .eq('creator_wallet', walletAddress)
        .order('created_at', { ascending: false })
        .limit(20)

      if (marketsError) throw marketsError

      // Get creator stats
      const { data: creatorStats, error: creatorError } = await supabase
        .from('creator_stats')
        .select('markets_created')
        .eq('creator_wallet', walletAddress)
        .single()

      // Combine stats with creator data
      const combinedStats = {
        ...(statsData || {
          wallet_address: walletAddress,
          activity_points: 0,
          total_bets: 0,
          win_rate: 0,
          total_profit: 0,
          total_volume: 0,
        }),
        markets_created: creatorStats?.markets_created || 0,
      }

      setStats(combinedStats)
      setRecentBets(betsData as RecentBet[])
      setCreatedMarkets(marketsData as CreatedMarket[])
    } catch (err) {
      console.error('Error fetching user profile:', err)
      setError(err instanceof Error ? err : new Error('Failed to fetch profile'))
    } finally {
      setLoading(false)
    }
  }, [walletAddress])

  useEffect(() => {
    fetchUserProfile()
  }, [fetchUserProfile])

  return {
    stats,
    recentBets,
    createdMarkets,
    loading,
    error,
  }
}

utility.code:
export function formatWallet(wallet: string): string {
  if (wallet.length <= 8) return wallet
  return `${wallet.slice(0, 4)}...${wallet.slice(-4)}`
}

export function copyToClipboard(text: string): Promise<void> {
  return navigator.clipboard.writeText(text)
}

